import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import {
  getCart,
  removeItem,
  addQuantity,
  subtractQuantity,
} from "./actions/cartActions";
import Recipe from "./Recipe";
class Cart extends Component {
  componentDidMount() {
    this.props.getCart();
  }
  //to remove the item completely
  handleRemove = (id) => {
    this.props.removeItem(id);
  };
  //to add the quantity
  handleAddQuantity = (id) => {
    this.props.addQuantity(id);
  };
  //to substruct from the quantity
  handleSubtractQuantity = (id) => {
    this.props.subtractQuantity(id);
  };
  render() {
    let addedItems = this.props.items.length ? (
      this.props.items.map((item) => {
        let showIncrement = item.book.quantity>0?false:true;
        return (
          <li className="collection-item avatar" key={item.id}>
            <div className="item-img">
              <img src={item.book.img} alt={item.book.img} className="" />
            </div>

            <div className="item-desc">
              <span className="title">{item.book.title}</span>
              <p>{item.desc}</p>
              <p>
                <b>Stock Quantity: {item.book.quantity}</b>
              </p>
              <p>
                <b>Quantity: {item.inCartQuantity}</b>
              </p>
              <div className="add-remove">
                <div style={{ display: showIncrement ? "block" : "none" }}>
                  {" "}
                  <Link to="/cart">
                    <i
                      className="material-icons"
                      onClick={() => {
                        this.handleAddQuantity(item.book.id);
                      }}
                    >
                      arrow_drop_up
                    </i>
                  </Link>
                </div><Link to="/cart">
                  <i
                    className="material-icons"
                    onClick={() => {
                      this.handleSubtractQuantity(item.book.id);
                    }}
                  >
                    arrow_drop_down
                  </i>
                </Link>
              </div>
              <button
                className="waves-effect waves-light btn pink remove"
                onClick={() => {
                  this.handleRemove(item.book.id);
                }}
              >
                Remove
              </button>
            </div>
          </li>
        );
      })
    ) : (
      <p>Nothing.</p>
    );
    return (
      <div className="container">
        <div className="cart">
          <h5>You have ordered:</h5>
          <ul className="collection">{addedItems}</ul>
        </div>
        <Recipe />
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    items: state.addedItems,
    //addedItems: state.addedItems
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    getCart: () => {
      dispatch(getCart());
    },
    removeItem: (id) => {
      dispatch(removeItem(id));
    },
    addQuantity: (id) => {
      dispatch(addQuantity(id));
    },
    subtractQuantity: (id) => {
      dispatch(subtractQuantity(id));
    },
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Cart);
