import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { userLogin } from './actions/cartActions'
import { Redirect } from 'react-router';
class Logout extends Component {
    constructor(props) {
        super(props);

        // reset login status
        //this.props.logout();

        this.state = {
            username: '',
            password: '',
            submitted: false,
            loginError:false,
            loggedIn:false
        };

    }
    handleChange = (name) => e => {
        if (name === "username") {
            this.setState({username:e.target.value});
          } else if (name === "password") {
            this.setState({password:e.target.value});
          }
    }
    handleLogin = ()=>{
        const { username, password } = this.state;
            this.props.userLogout(username, password);
            
       
    }
  render() {
    //this.setState({loggedIn : this.props.loggedIn,
   //   loginError : this.props.loginError})
 ///   if(this.state.loggedIn === true){
      //  return (<Redirect to="/" />);
   // }else{
    const { username, password } = this.state;
    let showError = this.state.loginError===true?true:false;
    return (
      <div className="container">
        <div className="cart">
          <div className="ex1">
            <h5>Confirm Logout?</h5>
            <form name="form" onSubmit={this.handleLogin}>
                    <div className={'form-group' }>
                        <button className="waves-effect waves-light btn pink remove" onClick={()=>{this.handleLogout()}}>Logout</button>
                      
                        
                    </div>
                </form>
            
            
          </div>
        </div>
      </div>
    );
 // }
}}
const mapStateToProps = (state) => {
  return {
    loggedIn: state.loggedIn,
    loginError: state.loginError,

  };
};
const mapDispatchToProps = (dispatch)=>{
    return{
        userLogout: (username) =>{dispatch(userLogout(username));},
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Logout);
