import React, { Component } from "react";
import { connect } from "react-redux";
class Details extends Component {
  render() {
    return (
        <div className="container">
                <div className="cart">
                    <h5>Title: {this.props.selectedItem.title}</h5>
                    
                                    <div className="item-img"> 
                                        <img src={this.props.selectedItem.img} alt={this.props.selectedItem.img} className=""/>
                                    </div>
                                
                                    <div className="item-desc">
                                        <span className="title">{this.props.selectedItem.title}</span>
                                        <p>{this.props.selectedItem.desc}</p>
                                        <p><b>Price: {this.props.selectedItem.price}$</b></p> 
                                        <p>
                                            <b>Quantity: {this.props.selectedItem.quantity}</b> 
                                        </p>
                                        
                                    
                                    </div>
                                    
                               
                </div> 
                      
            </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    items: state.items,
    selectedItem: state.selectedItem,
  };
};
export default connect(mapStateToProps)(Details);
