import { ADD_TO_CART,HANDLE_DETAIL,REMOVE_ITEM,SUB_QUANTITY,ADD_QUANTITY,ADD_SHIPPING,GET_BOOKS, BOOKS_ERROR} from './action-types/cart-actions'
import axios from 'axios'
//handle detail
export const addToCart= (id)=>{
    return{
        type: ADD_TO_CART,
        id
    }
}
//add cart action
export const addToDetail= (id)=>{
    return{
        type: HANDLE_DETAIL,
        id
    }
}
//remove item action
export const removeItem=(id)=>{
    return{
        type: REMOVE_ITEM,
        id
    }
}
//subtract qt action
export const subtractQuantity=(id)=>{
    return{
        type: SUB_QUANTITY,
        id
    }
}
//add qt action
export const addQuantity=(id)=>{
    return{
        type: ADD_QUANTITY,
        id
    }
}
export const getBooks = () => async dispatch => {
    
    try{
        const res = await axios.get(`http://localhost:8080/books`)
        dispatch( {
            type: GET_BOOKS,
            payload: res.data
        })
    }
    catch(e){
        dispatch( {
            type: BOOKS_ERROR,
            payload: console.log(e),
        })
    }

}
