import React, { Component } from "react";
import { connect } from "react-redux";
class Details extends Component {
  render() {
    return (
        <div className="container py-5">
        {/*title*/}
        <div className="row">
          <div className="col-10 mx-auto text-center text-slanted text-blue my-5">
            <h1>{this.props.selectedItem.title}</h1>
          </div>
        </div>
        {/*title*/}
        {/*book info*/}
        <div className="row">
          <div className="col-10 mx-auto col-md-6 my-3">
            <img src={this.props.selectedItem.title} className="img-fluid" alt="bookInfo" />
          </div>
          {/*book text*/}
          <div className="col-10 mx-auto col-md-6 my-3 text-capitalize">
            <h2>author: {this.props.selectedItem.title}</h2>
            <h4 className="text-blue">
              ISBN:{this.props.selectedItem.title}, Dimensions: {this.props.selectedItem.title}x{this.props.selectedItem.title}
            </h4>
            <h4 className="text-blue">
              Item ID : {this.props.selectedItem.title}
            </h4>
            <h4 className="text-blue">
              <strong>
                price: <span> $</span>
                {this.props.selectedItem.title}
              </strong>
            </h4>
            <p className="text-capitalize font-weight-bold mt-3 mb-0">
              some info about product:
            </p>
            <p className="text-muted lead">{this.props.selectedItem.title}</p>
            {/*buttons*/}
            <div>
              <Link to="/books">
                <ButtonContainer>Back to Books</ButtonContainer>
              </Link>
              <ButtonContainer disabled={this.props.selectedItem.title>0 || this.props.selectedItem.title<=0?true:false}
              onClick={()=>{
                 // value.addToCart(id);
                 // value.openModal(id);
              }}>
                  {this.props.selectedItem.title>0?'in cart':'add to cart'}
              </ButtonContainer>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    items: state.items,
    selectedItem: state.selectedItem,
  };
};
export default connect(mapStateToProps)(Details);
