import React, { Component } from "react";
import { connect } from "react-redux";
class Details extends Component {
  render() {
    return (
      <div className="container">
        <h3 className="center">Our items</h3>
        <div className="box">
          <div className="card" key={this.props.selectedItem.id}>
            <div className="card-image">
              <img
                src={this.props.selectedItem.img}
                alt={this.props.selectedItem.title}
              />

              <span className="card-title">
                {this.props.selectedItem.title}
              </span>
              <span
                to="/"
                className="btn-floating halfway-fab waves-effect waves-light red"
                onClick={() => {
                  this.handleClick(this.props.selectedItem.id);
                }}
              >
                <i className="material-icons">add</i>
              </span>
            </div>

            <div className="card-content">
              <p>{this.props.selectedItem.author}</p>
              <p>
                <b>Quantity: {this.props.selectedItem.price}</b>
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    items: state.items,
    selectedItem: state.selectedItem,
  };
};
export default connect(mapStateToProps)(Details);
