import React, { Component } from 'react'

class Details extends Component {
    render() {
        
            
            return(
            <div className="container">
                <h3 className="center">Our items</h3>
                <div className="box">
                <div className="card" key={selectedItem.id}>
                            <div className="card-image">
                                
                                <img src={selectedItem.img} alt={selectedItem.title}/>
                               
                                <span className="card-title">{selectedItem.title}</span>
                                <span to="/" className="btn-floating halfway-fab waves-effect waves-light red" onClick={()=>{this.handleClick(selectedItem.id)}}><i className="material-icons">add</i></span>
                            </div>
    
                            <div className="card-content">
                                <p>{selectedItem.author}</p>
                                <p><b>Quantity: {selectedItem.price}</b></p>
                            </div>
                     </div>
                </div>
            </div>
        )
        }

}
const mapStateToProps = (state)=>{
    return {
      items: state.items,
      selectedItem:state.selectedItem
    }
  }
  export default connect(mapStateToProps,mapDispatchToProps)(Details)
