import React, { Component } from "react";
import { connect } from "react-redux";
import { addToCart, getBooks, addToDetail } from "./actions/cartActions";
import { Link } from "react-router-dom";
class Home extends Component {
  componentDidMount() {
    this.props.getBooks();
  }
  handleClick = (id) => {
    this.props.addToCart(id);
  };

  handleDetail = (id) => {
    this.props.addToDetail(id);
  };

  render() {
    let isLoggedIn = this.props.loggedIn
    let itemList = this.props.items.map((item) => {
      
      let doAddToCart = (item.quantity<=0||isLoggedIn === false)?false:true;
      return (
        <div className="card" key={item.id}>
          <div className="card-image">
            <div
              onClick={() => {
                this.handleDetail(item.id);
              }}
            >
              <Link to="/details">
                <img src={item.img} alt={item.title} />
              </Link>
            </div>
           {/* <span className="card-title">{item.title}</span>*/}
           <div style={{ display: (doAddToCart ? 'block' : 'none') }}>
            <span
              to="/" 
              className="btn-floating halfway-fab waves-effect waves-light red"
              onClick={() => {
                this.handleClick(item.id);
              }}
            >
              <i className="material-icons">add</i>
            </span>
          </div>
          </div>
          <div className="card-content">
          <p><b>{item.title}</b></p>
            <p>{item.author}</p>
            <p>
              <b>Quantity: {item.quantity}</b>
            </p>
          </div>
        </div>
      );
    });

    return (
      <div className="container">
        <h3 className="center">Our Books</h3>
        <div  style={{ display: isLoggedIn === true ? "none" : "inline-block" ,color:"red"}}> <h7 className="center"><b>Please login to add to Cart!</b></h7></div>
        <div className="box">{itemList}</div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    items: state.items,
    selectedItem: state.selectedItem,
    loggedIn: state.loggedIn,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (id) => {
      dispatch(addToCart(id));
    },
    getBooks: () => {
      dispatch(getBooks());
    },
    addToDetail: (id) => {
      dispatch(addToDetail(id));
    },
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Home);
